import React from "react";
import DeleteIcon from "@material-ui/icons/Delete";

function Note(props) {
  // function handleClick(event) {
  //   props.onDelete(props.id);
  // }

  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      {/* <button onClick={handleClick}> */}
      <button onClick={()=>{
        props.onDelete(props.id);
      }}>
        <DeleteIcon />
      </button>
    </div>
  );
}

export default Note;
